import React, { useRef, useEffect, useState } from 'react';
import './App.css';

function App() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [ws, setWs] = useState(null);
  const [isConnected, setIsConnected] = useState(false);
  const [processedImage, setProcessedImage] = useState(null);
  const [fps, setFps] = useState(0);
  const [frameCount, setFrameCount] = useState(0);

  useEffect(() => {
    // Initialize webcam with higher resolution
    navigator.mediaDevices.getUserMedia({ 
      video: { 
        width: 640, 
        height: 480,
        frameRate: 30
      } 
    })
      .then(stream => {
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
        }
      })
      .catch(err => console.error('Camera access denied:', err));

    // Connect to WebSocket
    const websocket = new WebSocket('ws://localhost:8000/ws');

    websocket.onopen = () => {
      setIsConnected(true);
      setWs(websocket);
      console.log('üü¢ Connected to Project Mirage backend');
    };

    websocket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      setProcessedImage(data.processed_image);
      setFrameCount(prev => prev + 1);
    };

    websocket.onclose = () => {
      setIsConnected(false);
      console.log('üî¥ Disconnected from backend');
    };

    websocket.onerror = (error) => {
      console.error('WebSocket error:', error);
    };

    return () => {
      websocket.close();
    };
  }, []);

  useEffect(() => {
    if (!ws || !isConnected) return;

    const interval = setInterval(() => {
      if (videoRef.current && canvasRef.current && videoRef.current.readyState === 4) {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');

        canvas.width = videoRef.current.videoWidth;
        canvas.height = videoRef.current.videoHeight;

        ctx.drawImage(videoRef.current, 0, 0);

        const imageData = canvas.toDataURL('image/jpeg', 0.7);

        ws.send(JSON.stringify({
          image: imageData
        }));
      }
    }, 66); // ~15 FPS for better performance

    return () => clearInterval(interval);
  }, [ws, isConnected]);

  // Calculate FPS
  useEffect(() => {
    const interval = setInterval(() => {
      setFps(frameCount);
      setFrameCount(0);
    }, 1000);

    return () => clearInterval(interval);
  }, [frameCount]);

  return (
    <div className="App">
      <header className="App-header">
        <h1>üé≠ Project Mirage - Invisibility Cloak</h1>
        <p>‚ú® Real-time invisibility effects using advanced OpenCV algorithms</p>
        <div className="header-stats">
          <span>FPS: {fps}</span>
          <span>Status: {isConnected ? 'üü¢ Connected' : 'üî¥ Disconnected'}</span>
        </div>
      </header>

      <main className="main-content">
        <div className="video-container">
          <div className="video-section">
            <h3>üìπ Original Feed</h3>
            <video 
              ref={videoRef} 
              autoPlay 
              muted 
              playsInline
              className="video-feed"
            />
          </div>

          <div className="video-section">
            <h3>üé≠ Invisibility Effect</h3>
            {processedImage ? (
              <img 
                src={processedImage} 
                alt="Processed" 
                className="video-feed"
              />
            ) : (
              <div className="placeholder">
                {isConnected ? 'üîÑ Processing frames...' : 'üîå Connecting to backend...'}
              </div>
            )}
          </div>
        </div>

        <div className="instructions">
          <h3>üìã How to Use:</h3>
          <div className="instruction-grid">
            <div className="instruction-item">
              <span className="step">1</span>
              <p>Hold a <strong>red colored cloth</strong> in front of camera</p>
            </div>
            <div className="instruction-item">
              <span className="step">2</span>
              <p>Move <strong>slowly</strong> for best invisibility effect</p>
            </div>
            <div className="instruction-item">
              <span className="step">3</span>
              <p>Ensure <strong>good lighting</strong> conditions</p>
            </div>
            <div className="instruction-item">
              <span className="step">4</span>
              <p>Wait for <strong>30 frames</strong> for background capture</p>
            </div>
          </div>
        </div>
      </main>

      <footer className="footer">
        <p>üé≠ Built with ‚ù§Ô∏è for Computer Vision Excellence</p>
        <p><em>Making Magic Real Through Technology</em></p>
      </footer>

      <canvas ref={canvasRef} style={{ display: 'none' }} />
    </div>
  );
}

export default App;
