import React, { useRef, useEffect, useState } from 'react';
import './App.css';

function App() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [ws, setWs] = useState(null);
  const [isConnected, setIsConnected] = useState(false);
  const [processedImage, setProcessedImage] = useState(null);
  const [fps, setFps] = useState(0);
  const [frameCount, setFrameCount] = useState(0);
  const [menuOpen, setMenuOpen] = useState(false);

  useEffect(() => {
    // Initialize webcam with higher resolution
    navigator.mediaDevices.getUserMedia({ 
      video: { 
        width: 640, 
        height: 480,
        frameRate: 30
      } 
    })
      .then(stream => {
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
        }
      })
      .catch(err => console.error('Camera access denied:', err));

    // Connect to WebSocket
    const websocket = new WebSocket('ws://localhost:8000/ws');

    websocket.onopen = () => {
      setIsConnected(true);
      setWs(websocket);
      console.log('üü¢ Connected to Project Mirage backend');
    };

    websocket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      setProcessedImage(data.processed_image);
      setFrameCount(prev => prev + 1);
    };

    websocket.onclose = () => {
      setIsConnected(false);
      console.log('üî¥ Disconnected from backend');
    };

    websocket.onerror = (error) => {
      console.error('WebSocket error:', error);
    };

    return () => {
      websocket.close();
    };
  }, []);

  useEffect(() => {
    if (!ws || !isConnected) return;

    const interval = setInterval(() => {
      if (videoRef.current && canvasRef.current && videoRef.current.readyState === 4) {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');

        canvas.width = videoRef.current.videoWidth;
        canvas.height = videoRef.current.videoHeight;

        ctx.drawImage(videoRef.current, 0, 0);

        const imageData = canvas.toDataURL('image/jpeg', 0.7);

        ws.send(JSON.stringify({
          image: imageData
        }));
      }
    }, 66); // ~15 FPS for better performance

    return () => clearInterval(interval);
  }, [ws, isConnected]);

  // Calculate FPS
  useEffect(() => {
    const interval = setInterval(() => {
      setFps(frameCount);
      setFrameCount(0);
    }, 1000);

    return () => clearInterval(interval);
  }, [frameCount]);

  return (
    <div className="App">
      <nav className="navbar">
        <div className="nav-container">
          <div className="nav-logo">
            <span className="logo-icon">üé≠</span>
            <span className="logo-text">Project Mirage</span>
          </div>

          <div className={`nav-menu ${menuOpen ? 'active' : ''}`}>
            <a href="#home" className="nav-link" onClick={() => setMenuOpen(false)}>Home</a>
            <a href="#demo" className="nav-link" onClick={() => setMenuOpen(false)}>Live Demo</a>
            <a href="#about" className="nav-link" onClick={() => setMenuOpen(false)}>About</a>
            <a href="#contact" className="nav-link" onClick={() => setMenuOpen(false)}>Contact</a>
          </div>

          <div className="nav-stats">
            <span className="stat-item">FPS: {fps}</span>
            <span className={`stat-item ${isConnected ? 'connected' : 'disconnected'}`}>
              {isConnected ? 'üü¢ Connected' : 'üî¥ Disconnected'}
            </span>
          </div>

          <div className="nav-toggle" onClick={() => setMenuOpen(!menuOpen)}>
            <span className="bar"></span>
            <span className="bar"></span>
            <span className="bar"></span>
          </div>
        </div>
      </nav>

      <header className="hero-section" id="home">
        <div className="hero-content">
          <h1 className="hero-title">üé≠ Project Mirage</h1>
          <h2 className="hero-subtitle">AI-Powered Invisibility Cloak System</h2>
          <p className="hero-description">‚ú® Real-time invisibility effects using advanced OpenCV algorithms and HSV color detection technology</p>
          <div className="hero-buttons">
            <button className="btn-primary" onClick={() => document.getElementById('demo').scrollIntoView({behavior: 'smooth'})}>
              Try Live Demo
            </button>
            <button className="btn-secondary" onClick={() => window.open('https://github.com/abhishekgiri04', '_blank')}>
              View on GitHub
            </button>
          </div>
        </div>
      </header>

      <main className="main-content" id="demo">
        <div className="section-header">
          <h2>üé¨ Live Invisibility Demo</h2>
          <p>Experience real-time invisibility effects powered by computer vision</p>
        </div>
        <div className="video-container">
          <div className="video-section">
            <h3>üìπ Original Feed</h3>
            <video 
              ref={videoRef} 
              autoPlay 
              muted 
              playsInline
              className="video-feed"
            />
          </div>

          <div className="video-section">
            <h3>üé≠ Invisibility Effect</h3>
            {processedImage ? (
              <img 
                src={processedImage} 
                alt="Processed" 
                className="video-feed"
              />
            ) : (
              <div className="placeholder">
                {isConnected ? 'üîÑ Processing frames...' : 'üîå Connecting to backend...'}
              </div>
            )}
          </div>
        </div>

        <div className="instructions">
          <h3>üìã How to Use:</h3>
          <div className="instruction-grid">
            <div className="instruction-item">
              <span className="step">1</span>
              <p>Hold a <strong>red colored cloth</strong> in front of camera</p>
            </div>
            <div className="instruction-item">
              <span className="step">2</span>
              <p>Move <strong>slowly</strong> for best invisibility effect</p>
            </div>
            <div className="instruction-item">
              <span className="step">3</span>
              <p>Ensure <strong>good lighting</strong> conditions</p>
            </div>
            <div className="instruction-item">
              <span className="step">4</span>
              <p>Wait for <strong>30 frames</strong> for background capture</p>
            </div>
          </div>
        </div>
      </main>

      <section className="about-section" id="about">
        <div className="container">
          <div className="section-header">
            <h2>üî¨ How It Works</h2>
            <p>Advanced computer vision technology behind the magic</p>
          </div>

          <div className="tech-grid">
            <div className="tech-card">
              <div className="tech-icon">üé®</div>
              <h3>HSV Color Detection</h3>
              <p>Advanced color space processing for accurate red cloth detection with dual range masking</p>
            </div>

            <div className="tech-card">
              <div className="tech-icon">üîÑ</div>
              <h3>Real-time Processing</h3>
              <p>30+ FPS video processing with optimized OpenCV algorithms and WebSocket communication</p>
            </div>

            <div className="tech-card">
              <div className="tech-icon">üé≠</div>
              <h3>Background Subtraction</h3>
              <p>Dynamic background capture and pixel replacement for seamless invisibility effects</p>
            </div>

            <div className="tech-card">
              <div className="tech-icon">‚öôÔ∏è</div>
              <h3>Morphological Operations</h3>
              <p>Advanced noise reduction and mask refinement for clean invisibility boundaries</p>
            </div>
          </div>
        </div>
      </section>

      <footer className="footer" id="contact">
        <div className="footer-content">
          <div className="footer-section">
            <div className="footer-logo">
              <span className="logo-icon">üé≠</span>
              <span className="logo-text">Project Mirage</span>
            </div>
            <p className="footer-description">
              Making magic real through cutting-edge computer vision technology. 
              Experience the future of augmented reality with our AI-powered invisibility cloak.
            </p>
            <div className="social-links">
              <a href="https://github.com/abhishekgiri04" target="_blank" rel="noopener noreferrer" className="social-link">
                <span>üêà</span> GitHub
              </a>
              <a href="https://www.linkedin.com/in/abhishek-giri04/" target="_blank" rel="noopener noreferrer" className="social-link">
                <span>üíº</span> LinkedIn
              </a>
              <a href="https://t.me/AbhishekGiri7" target="_blank" rel="noopener noreferrer" className="social-link">
                <span>üí¨</span> Telegram
              </a>
            </div>
          </div>

          <div className="footer-section">
            <h3>Technology Stack</h3>
            <ul className="tech-list">
              <li><span>üêç</span> Python FastAPI</li>
              <li><span>‚öõÔ∏è</span> React.js</li>
              <li><span>üì∑</span> OpenCV</li>
              <li><span>üî¢</span> NumPy</li>
              <li><span>üåê</span> WebSocket</li>
            </ul>
          </div>

          <div className="footer-section">
            <h3>Features</h3>
            <ul className="feature-list">
              <li>‚ú® Real-time Processing</li>
              <li>üé® HSV Color Detection</li>
              <li>üîÑ Background Subtraction</li>
              <li>üì± Responsive Design</li>
              <li>üöÄ High Performance</li>
            </ul>
          </div>

          <div className="footer-section">
            <h3>Performance Metrics</h3>
            <div className="metrics">
              <div className="metric">
                <span className="metric-value">30+</span>
                <span className="metric-label">FPS</span>
              </div>
              <div className="metric">
                <span className="metric-value">95%</span>
                <span className="metric-label">Accuracy</span>
              </div>
              <div className="metric">
                <span className="metric-value">&lt;100ms</span>
                <span className="metric-label">Latency</span>
              </div>
            </div>
          </div>
        </div>

        <div className="footer-bottom">
          <div className="footer-bottom-content">
            <p>&copy; 2025 Project Mirage. All Rights Reserved.</p>
            <p>üé≠ Built with ‚ù§Ô∏è for Computer Vision Excellence | <em>Making Magic Real Through Technology</em></p>
          </div>
        </div>
      </footer>

      <canvas ref={canvasRef} style={{ display: 'none' }} />
    </div>
  );
}

export default App;
